var names = new Array();

function doRainbow(e) {
    var t = ["red", "#FF9100", "yellow", "#2FFF00", "cyan", "#005AFF", "#9A00FF", "magenta"],
        r = 0,
        o = $(e).text(),
        n = 0,
        a = o.replace(" ", "");
    names.push(o);
    $(e).empty();
    for (var c = 0; c < o.length; c++) letter = $("<span>", {
        text: o.charAt(c),
        "class": a + "_" + c,
        css: {
            color: "red"
        }
    }), $(e).append(letter);
    ids.indexOf(a) > -1 || (ids.push(a), setInterval(function() {
        var e, c;
        r > t.length - 1 ? (r = 0, c = t[r]) : c = t[r], e = 0 > r - 1 ? t[t.length - 1] : t[r - 1];
        for (var s = 0; s < o.length; s++) {
            s > n ? $("." + a + "_" + s).each(function(t, r) {
                $(r).css("color", e)
            }) : $("." + a + "_" + s).each(function(e, t) {
                $(t).css("color", c)
            })
        }
        for (n++;
            " " == o.charAt(n);) n++;
        n >= o.length && (n = 0, r++)
    }, 130))
}
var ids = [];
$(function() {
    $(".unlimited-access").each(function(e, t) {
        $(t).removeClass("unlimited-access"), $(t).css("text-shadow","0px 0px 10px #000000"), doRainbow(t)
    }), setInterval(function() {
        $(".unlimited-access").each(function(e, t) {
          if($.inArray($(t).text(), names) == -1){
            doRainbow(t);
            return;
          }
            var r = $(t).text(),
                o = r.replace(" ", "");
            $(t).removeClass("unlimited-access"), $(t).css("text-shadow","0px 0px 10px #000000"), $(t).empty();
            for (var n = 0; n < r.length; n++) letter = $("<span>", {
                text: r.charAt(n),
                "class": o + "_" + n,
                css: {
                    color: "red"
                }
            }), $(t).append(letter)
        })
    }, 100)
});